import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import Cart from './../components/Cart';
import * as Message from '../constants/Messages';
import CartItem from './../components/CartItem';
import CartResult from './../components/CartResult';
import { deleteProductInCart, actionChangeMessage, updateProductInCart} from './../actions/index';

class CartContainer extends Component {
    render() {
        var { cart } = this.props;
        return (
            <Cart>
                { this.showCartItem(cart) }
                { this.showTotal(cart) }
            </Cart>
        );
    }

    showCartItem = (cart) => {
        var {onDeleteProductInCart, onChangeMessage} = this.props;
        var result = 
            <tr>
                <td>{Message.MSG_CART_EMPTY}</td>
            </tr>;

        if(cart.length > 0){
            result = cart.map((item, index) => {
                return (
                    <CartItem 
                        key = {index}
                        item = {item}
                        onDeleteProductInCart = {onDeleteProductInCart}
                        onChangeMessage = {onChangeMessage}
                    />
                );
            });
        }

        return result;
    }

    showTotal = (cart) => {
        var result = null;

        if(cart.length > 0) {
            result = <CartResult cart={cart}/>
        }

        return result;
    }
}

CartContainer.propTypes = {
    cart : PropTypes.arrayOf(
        PropTypes.shape({
            product : PropTypes.shape({
                id : PropTypes.number.isRequired,
                name : PropTypes.string.isRequired,
                image : PropTypes.string.isRequired,
                description : PropTypes.string.isRequired,
                price : PropTypes.number.isRequired,
                inventory : PropTypes.number.isRequired,
                rating : PropTypes.number.isRequired
            }).isRequired,
            quantity : PropTypes.number.isRequired
        })
    ).isRequired
}

const mapStateToProps = (state) => {
    return {
        cart : state.cart
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        onDeleteProductInCart : (product) => {
            dispatch(deleteProductInCart(product));
        },
        onChangeMessage : (message) => {
            dispatch(actionChangeMessage(message));
        },
        onUpdateProductInCart : (product, quantity) => {
            dispatch(updateProductInCart(product,quantity));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps) (CartContainer);
